const navbarBtn=document.querySelector("#navbar__button"),navbarMenu=document.querySelector("#navbar__menu");navbarBtn.addEventListener("click",()=>{navbarMenu.classList.contains("navbar--mobile--close")?(navbarMenu.classList.remove("navbar--mobile--close"),navbarBtn.classList.add("navbar__menu--open")):(navbarMenu.classList.add("navbar--mobile--close"),navbarBtn.classList.remove("navbar__menu--open"))});